import React from 'react';
import './App.css';
import Boundingbox from 'react-bounding-box';

class App extends React.Component {
  state = {
    selected: false,
    x: -1,
    y: -1,
    w: -1,
    h: -1
  };

  canvas = null;
  ctx = null;
  dirty = false;
  drag = false;
  startX = -1;
  startY = -1;
  curX = -1;
  curY = -1;
  width = "640"
  height= "480"

  componentDidMount() {
    this.init();
  }

  init() {
    var canvas = document.getElementById('canvas2');
    var myImage = new Image();
    myImage.src = "https://image.shutterstock.com/image-photo/bright-spring-view-cameo-island-260nw-1048185397.jpg";
    var ctx = canvas.getContext('2d');
    myImage.onload = function() {
      ctx.drawImage(myImage, 0, 0, 300, 200);
    }
    this.addMouseEvents()
  };

  addMouseEvents() {
    document.addEventListener('mousedown', this.onMouseDown, false);
    document.addEventListener('mousemove', this.onMouseMove, false);
    document.addEventListener('mouseup', this.onMouseUp, false);
  }

  componentWillUnmount() {
    this.removeMouseEvents()
  }

  removeMouseEvents() {
    document.removeEventListener('mousedown', this.onMouseDown, false);
    document.removeEventListener('mousemove', this.onMouseMove, false);
    document.removeEventListener('mouseup', this.onMouseUp, false);
  }

  updateCanvas = () => {
    if (this.isDrag) {
      requestAnimationFrame(this.updateCanvas)
    }
    if (! this.isDirty) {
      return
    }

    this.ctx.clearRect(0, 0, this.width, this.height)
    if (this.isDrag) {      
      const rect = {
        x: this.startX,
        y: this.startY,
        w: this.curX - this.startX,
        h: this.curY - this.startY,
      }
      this.ctx.strokeRect(rect.x, rect.y, rect.w, rect.h)  
    }  
    this.isDirty = false
  };

  onSelected = (rect) => {
    this.setState({
      selected: true,
      ...rect
    })
  };

  getSelectionStr() {
    if (this.state.selected) {
      const state = this.state
      return `x: ${state.x}, y: ${state.y}, w: ${state.w}, h: ${state.h}`
    }
  }

  onMouseDown = (e) => {
    this.isDrag = true
    this.curX = this.startX = e.offsetX
    this.curY = this.startY = e.offsetY
    requestAnimationFrame(this.updateCanvas)
  };

  onMouseMove = (e) => {
    this.curX = e.offsetX
    this.curY = e.offsetY
    this.isDirty = true
  };

  onMouseUp = (e) => {
    this.isDrag = false
    this.isDirty = true

    const rect = {
      x: Math.min(this.startX, this.curX),
      y: Math.min(this.startY, this.curY),
      w: Math.abs(e.offsetX - this.startX),
      h: Math.abs(e.offsetY - this.startY),
    }
    this.onSelected(rect)
  };


  // mousedown(e){
  //     this.startX = e.pageX - 8;
  //     this.startY = e.pageY - 8;
  //     this.drag = true;
  // }

  // mouseup(){
  //   this.drag = false;
  // }

  // mousemove(e){
  //   let canvas = document.getElementById('canvas2');
  //   var ctx = canvas.getContext('2d');
  //   var myImage = new Image();
  //   myImage.src = "https://image.shutterstock.com/image-photo/bright-spring-view-cameo-island-260nw-1048185397.jpg";
  //   if (this.drag) {
  //       console.log('in ifffffff')
  //       ctx.clearRect(0, 0, 500, 500);
  //       myImage.onload = function() {
  //         ctx.drawImage(myImage, 0, 0, 300, 200);
  //       }
  //       this.rect.w = (e.pageX - 8) - this.rect.startX;
  //       this.rect.h = (e.pageY - 8) - this.rect.startY;
  //     ctx.strokeStyle = 'red';
  //     ctx.strokeRect(this.rect.startX, this.rect.startY, this.rect.w, this.rect.h);
  //   }
  // }

  selectFile = (e) => {
    var url = URL.createObjectURL(e.target.files[0]);
    this.setState({
      url
    })
    const bref = this.refs.canvas;
    let bcanvas = bref.canvas;
    bcanvas.width = 640;
    bcanvas.height = 425;
    const ctx = bcanvas.getContext("2d");
  };


  render(){
    // var rect = {};
    // var drag = false;
    const params = {
      image: 'https://image.shutterstock.com/image-photo/mountains-during-sunset-beautiful-natural-260nw-407021107.jpg',
      boxes: [
        {coord: [this.state.inp1, this.state.inp2, this.state.inp3, this.state.inp4], label: "A"},
        // {coord: [300, 0, 250, 250], label: "B"},
        // {coord: [700, 0, 300, 25], label: "C"},
        // {coord: [1100, 0, 25, 300], label: "D"}
        // onMouseUp={this.mouseup} onMouseMove={this.mousemove}
      ],
      options: {
        colors: {
          normal: 'rgba(255,225,255,1)',
          selected: 'rgba(0,225,204,1)',
          unselected: 'rgba(100,100,100,1)'
        },
        style: {
          maxWidth: '100%',
          maxHeight: '90vh',
        },
        showLabels: false
      }
    };

    return (
      <div className="App">
        <input type="file" id="inp" onChange={this.selectFile} />
        <br /><br />
        Enter coords:{' '}For Example: {' '} 300, 0, 250, 100
        <br />
        <input type="number" id="inp1" onChange={(e) => {this.setState({ [e.target.id]: parseInt(e.target.value) })}} />
        <input type="number" id="inp2" onChange={(e) => {this.setState({ [e.target.id]: parseInt(e.target.value) })}} />
        <input type="number" id="inp3" onChange={(e) => {this.setState({ [e.target.id]: parseInt(e.target.value) })}} />
        <input type="number" id="inp4" onChange={(e) => {this.setState({ [e.target.id]: parseInt(e.target.value) })}} />
        <br /><br />
        <Boundingbox
          ref="canvas"
          image={params.image}
          boxes={params.boxes}
          options={params.options}
        />
        <canvas ref="canvas2" id="canvas2" width="640" height="480" onSelected={this.onSelected} onMouseDown={this.mousedown.bind(this)} onMouseUp={this.mouseup.bind(this)} onMouseMove={this.mousemove.bind(this)} />
        {this.getSelectionStr()}
      </div>
    );
  }
}

export default App;
